const { createPublicClient, http } = require('viem');
const { sepolia } = require('viem/chains');

const client = createPublicClient({
  chain: sepolia,
  transport: http('https://ethereum-sepolia-rpc.publicnode.com')
});

const sig = "";
const address = "0xCc9877f551A39797fF0afd937BE43Cc77631f274";
const message = "Resonate Sign-In\nAddress: 0xCc9877f551A39797fF0afd937BE43Cc77631f274\nNonce: dc6b442d-5e69-4bc7-80d9-12f126a51af0\nIssued At: 2026-02-19T23:15:42.124Z";

async function run() {
  try {
    const ok = await client.verifyMessage({ address, message, signature: sig });
    console.log("verifyMessage returned:", ok);
  } catch (e) {
    console.error("verifyMessage threw:", e.message);
  }
}
run();
